import fs from 'fs'
import path from 'path'

// CLASSES_PATH is useful when you want to get the path to a specific file
export const CLASSES_PATH = path.join(process.cwd(), 'src/classes')

// classFilePaths is the list of all mdx files inside the CLASSES_PATH directory
export const classFilePaths = fs
    .readdirSync(CLASSES_PATH)
    // Only include md(x) files
    .filter((path) => /\.mdx?$/.test(path))
