import fs from 'fs'
import matter from 'gray-matter'
import hydrate from 'next-mdx-remote/hydrate'
import renderToString from 'next-mdx-remote/render-to-string'
import dynamic from 'next/dynamic'
import Head from 'next/head'
import Link from 'next/link'
import path from 'path'
import CustomLink from '../../components/CustomLink'
import Layout from '../../components/Layout'
import { coursesFilePaths, COURSES_PATH } from '../../utils/mdxUtils'
import Header from "../../components/Header/Header";
import HeaderLinks from "../../components/Header/HeaderLinks";
import Parallax from "../../components/Parallax/Parallax";
import GridContainer from "../../components/Grid/GridContainer";
import GridItem from "../../components/Grid/GridItem";
import profile from "../../assets/img/apple-icon.png";
import Footer from "../../components/Footer/Footer";
import React from "react";

import classNames from "classnames";

const components = {
    a: CustomLink,
    TestComponent: dynamic(() => import('../../components/TestComponent')),
    Head,
}

function CourseDescription({ content, frontMatter }) {
    return (
        <Layout>
            <div>
                <nav>
                    <Link href="/courses">
                        <a>Cursos</a>
                    </Link>
                </nav>
            </div>
            <div className="ccourse-header">
                <h1>{frontMatter.title}</h1>
                {frontMatter.description && (
                    <p className="description">{frontMatter.description}</p>
                )}
            </div>
            <main>{content}</main>

            <style jsx>{`
        .ccourse-header h1 {
          margin-bottom: 0;
        }
        .ccourse-header {
          margin-bottom: 2rem;
        }
        .description {
          opacity: 0.6;
        }
      `}</style>
        </Layout>
    );
}

export default function CoursePage({ source, frontMatter }) {
    const content = hydrate(source, { components })
    const courses = useStyles();
    const { ...rest } = props;
    const imageCourses = classNames(
        courses.imgRaised,
        courses.imgRoundedCircle,
        courses.imgFluid
    );
    return (
        <div>
            <Header
                color="transparent"
                brand="NextJS Material Kit"
                rightLinks={<HeaderLinks />}
                fixed
                changeColorOnScroll={{
                    height: 200,
                    color: "white"
                }}
                {...rest}
            />
            <Parallax small filter image={require("src/assets/img/profile-bg.jpg")} />
            <div className={classNames(courses.main, courses.mainRaised)}>
                <div>
                    <div className={courses.container}>
                        <GridContainer justify="center">
                            <GridItem xs={12} sm={12} md={6}>
                                <div className={courses.profile}>
                                    <div>
                                        <img src={profile} alt="..." className={imageCourses} />
                                    </div>
                                    <div className={courses.name}>
                                        <h2 className={courses.title}>Cursos</h2>
                                    </div>
                                </div>
                            </GridItem>
                        </GridContainer>
                        <CourseDescription content={content} frontMatter={frontMatter} />
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    )
}

export const getStaticProps = async ({ params }) => {
    const coursesFilePaths = path.join(COURSES_PATH, `${params.slug}.mdx`)
    const source = fs.readFileSync(coursesFilePaths)

    const { content, data } = matter(source)

    const mdxSource = await renderToString(content, {
        components,
        // Optionally pass remark/rehype plugins
        mdxOptions: {
            remarkPlugins: [],
            rehypePlugins: [],
        },
        scope: data,
    })

    return {
        props: {
            source: mdxSource,
            frontMatter: data,
        },
    }
}

export const getStaticPaths = async () => {
    const paths = coursesFilePaths
        .map((path) => path.replace(/\.mdx?$/, ''))
        .map((slug) => ({ params: { slug } }))

    return {
        paths,
        fallback: false,
    }
}
